mapper.select:
select
  id, task_name, store_selection_type, user_range_type, task_type, affiliated_company_type, quantity_allotted, execution_mode, script_content, task_objective_ratio, task_start_time, task_end_time, return_visit_result, status, create_time, update_time
from task_management

    
        and id = #{id}
    
    
        and task_name = #{taskName}
    
    
        and store_selection_type = #{storeSelectionType}
    
    
        and user_range_type = #{userRangeType}
    
    
        and task_type = #{taskType}
    
    
        and affiliated_company_type = #{affiliatedCompanyType}
    
    
        and quantity_allotted = #{quantityAllotted}
    
    
        and execution_mode = #{executionMode}
    
    
        and script_content = #{scriptContent}
    
    
        and task_objective_ratio = #{taskObjectiveRatio}
    
    
        and task_start_time = #{taskStartTime}
    
    
        and task_end_time = #{taskEndTime}
    
    
        and return_visit_result = #{returnVisitResult}
    
    
        and status = #{status}
    
    
        and create_time = #{createTime}
    
    
        and update_time = #{updateTime}
    

limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from task_management

    
        and id = #{id}
    
    
        and task_name = #{taskName}
    
    
        and store_selection_type = #{storeSelectionType}
    
    
        and user_range_type = #{userRangeType}
    
    
        and task_type = #{taskType}
    
    
        and affiliated_company_type = #{affiliatedCompanyType}
    
    
        and quantity_allotted = #{quantityAllotted}
    
    
        and execution_mode = #{executionMode}
    
    
        and script_content = #{scriptContent}
    
    
        and task_objective_ratio = #{taskObjectiveRatio}
    
    
        and task_start_time = #{taskStartTime}
    
    
        and task_end_time = #{taskEndTime}
    
    
        and return_visit_result = #{returnVisitResult}
    
    
        and status = #{status}
    
    
        and create_time = #{createTime}
    
    
        and update_time = #{updateTime}
